import { useState } from "react";


function App() {
  const [city, setCity] = useState("")
  const [weatherForcast, setweatherForcast] = useState(null)


  const handleChange = (e) => {
    setCity(e.target.value)
  }


  const handleSearch = (e) => {
    fetch(`https://api.weatherapi.com/v1/current.json?key=db8f580a6f4a489daf7121938231006&q=${city}&lang=pt`)
      .then((response) => {
        if (response.status === 200) {
          return response.json()
        }
      })
      .then((data)=>{
        console.log(data)
        setweatherForcast(data)
        })
};
const getWeatherIcon = () => {
  if (!weatherForcast) return null;

  const conditionCode = weatherForcast.current.condition.text;

  if (conditionCode === "Parcialmente Nublado") {
    // Clear
    return <img src="nublado.jpg" alt=""></img>;
  } else if (conditionCode === 1003 || conditionCode === 1006) {
    // Partly cloudy
    return "cloudy.jpg";
  } else if (conditionCode === 1063 || conditionCode === 1150) {
    // Patchy rain possible
    return "rainy.jpg";
  } else {
    return "default.jpg";
  }
};


return (
  <div>
    <nav className="navbar navbar-expand-md navbar-dark bg-dark mb-4">
      <a className="navbar-brand text-white" href="#top">
        Previsão do tempo
      </a>
    </nav>

    <main className="container">
      <div className='bg-light -5'>
        <h1>
          <p>Verifique a previsão do tempo em sua cidade</p>
        </h1>
        <p className="lead">
          Digite o nome da sua cidade no campo abaixo

        </p>
        <div className="row mb-4">
          <div className="col-md-6">
            <input
              onChange={handleChange}
              className="form-control" value={city} />
          </div>
        </div>
        <button
          onClick={handleSearch}
          className="btn btn-primary btn-lg">
          Pesquisar

        </button>

        {weatherForcast ? (
          <div>
          <div className="mt-4 d-flex align-items-center">
            <div>
              <img src={weatherForcast.current.condition.icon}  alt="icon" />

            </div>
            <div>
              <img src={getWeatherIcon()} alt="WeatherIcon"></img>
              </div>
            <div>
            <h3>Atualmente em <span>{city}</span> o dia está: {weatherForcast.current.condition.text}</h3>
            <p className="loading"> Temp: {weatherForcast.current.temp_c}º</p>
            <p className="lead"> Umidade: {weatherForcast.current.humidity}</p>
            <p className="lead"> País: {weatherForcast.location.country}</p>
            <p></p>
            </div>

          </div>
        </div>
        ) :null } 
      </div>
    </main>
  </div>
);
}

export default App;
