import { useState } from "react";

function App() {
  const [city, setCity] = useState("");
  const [weatherForcast, setWeatherForcast] = useState(null);

  const handleChange = (e) => {
    setCity(e.target.value);
  };

  const handleSearch = (e) => {
    fetch(
      `https://api.weatherapi.com/v1/current.json?key=db8f580a6f4a489daf7121938231006&q=${city}&lang=pt`
    )
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data);
        setWeatherForcast(data);
      });
  };

  const getWeatherIcon = () => {
    if (!weatherForcast) return null;

    const conditionText = weatherForcast.current.condition.text;

    if (conditionText === "Sol") {
      // Clear
      return <img src="sol.jpg" alt="Sol" />;
    } else if (
      conditionText === "Parcialmente nublado" ||
      conditionText === "Nublado"
    ) {
      // Partly cloudy or cloudy
      return <img src="nublado.jpg" alt="Nublado" />;
    } else if (conditionText.includes("chuva")) {
      // Rain
      return <img src="chuva.jpg" alt="Chuva" />;
    } else {
      return null;
    }
  };

  return (
    <div>
      <nav className="navbar navbar-expand-md navbar-dark bg-dark mb-4">
        <a className="navbar-brand text-white" href="#top">
          Previsão do tempo
        </a>
      </nav>

      <main className="container">
        <div className="bg-light -5">
          <h1>
            <p>Verifique a previsão do tempo em sua cidade</p>
          </h1>
          <p className="lead">Digite o nome da sua cidade no campo abaixo</p>
          <div className="row mb-4">
            <div className="col-md-6">
              <input
                onChange={handleChange}
                className="form-control"
                value={city}
              />
            </div>
          </div>
          <button onClick={handleSearch} className="btn btn-primary btn-lg">
            Pesquisar
          </button>

          {weatherForcast && (
            <div>
              <div className="mt-4 d-flex align-items-center">
                <div>
                  <img
                    src={weatherForcast.current.condition.icon}
                    alt="icon"
                  />
                </div>
                <div>{getWeatherIcon()}</div>
                <div>
                  <h3>
                    Atualmente em <span>{city}</span> o dia está:{" "}
                    {weatherForcast.current.condition.text}
                  </h3>
                  <p className="loading">
                    Temp: {weatherForcast.current.temp_c}º
                  </p>
                  <p className="lead">
                    Umidade: {weatherForcast.current.humidity}
                  </p>
                  <p className="lead">
                    País: {weatherForcast.location.country}
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
