import { useState } from "react";
import Condition from "./Condition";


function App() {
  const [city, setCity] = useState("")
  const [weatherForcast, setweatherForcast] = useState(null)


  const handleChange = (e) => {
    setCity(e.target.value)
  }


  const handleSearch = (e) => {
    fetch(`https://api.weatherapi.com/v1/current.json?key=db8f580a6f4a489daf7121938231006&q=${city}&lang=pt`)
      .then((response) => {
        if (response.status === 200) {
          return response.json()
        }
      })
      .then((data)=>{
        setweatherForcast(data)
        })
};


const condition = () => {
  if (weatherForcast.current.condition.text === 20)
  return <img src="./img/nublado.jpg" alt="nublado"/>
}
return (
  <div>
    <nav className="navbar navbar-expand-md navbar-dark bg-dark mb-4">
      <a className="navbar-brand text-white" href="#top">
        Previsão do tempo
      </a>
    </nav>

    <main className="container">
      <div className='bg-light -5'>
        <h1>
          <p>Verifique a previsão do tempo em sua cidade</p>
        </h1>
        <p className="lead">
          Digite o nome da sua cidade no campo abaixo

        </p>
        <div className="row mb-4">
          <div className="col-md-6">
            <input
              onChange={handleChange}
              className="form-control" value={city} />
          </div>
        </div>
        <button
          onClick={handleSearch}
          className="btn btn-primary btn-lg">
          Pesquisar

        </button>

        {weatherForcast ? (
          <div>
          <div className="mt-4 d-flex align-items-center">
            <div>
              <img src={weatherForcast.current.condition.icon} alt="icon" />
            </div>

            <div>
            <h3>Hoje o dia está: {weatherForcast.current.condition.text}</h3>
            <p className="lead"> Temp: {weatherForcast.current.temp_c}º</p>
            </div>

          </div>
        </div>
        ) :null } 
      </div>
    </main>
  </div>
);
}

export default App;
