import React, { useState } from "react";
import "./WeatherApp.css";

function App() {
  const [city, setCity] = useState("");
  const [weatherForecast, setWeatherForecast] = useState(null);

  const handleChange = (e) => {
    setCity(e.target.value);
  };

  const handleSearch = () => {
    fetch(
      `https://api.weatherapi.com/v1/current.json?key=db8f580a6f4a489daf7121938231006&q=${city}&lang=pt`
    )
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        }
      })
      .then((data) => {
        console.log(data);
        setWeatherForecast(data);
      });
  };

  const getWeatherIcon = () => {
    if (!weatherForecast) return null;

    const conditionCode = weatherForecast.current.condition.text;

    if (conditionCode === "Sol") {
      return "sol.jpg";
    } else if (conditionCode === "Parcialmente nublado" || conditionCode === "Nublado") {
      return <div className="weather-background cloudy"></div>;
    } else if (conditionCode === 1063 || conditionCode === 1150) {
      return "chuva.jpg";
    } else {
      return "default.jpg";
    }
  };

  return (
    <div>
      <nav className="navbar navbar-expand-md navbar-dark bg-dark mb-4">
        <a className="navbar-brand text-white" href="#top">
          Previsão do tempo
        </a>
      </nav>

      <main className="container">
        <div className="weather-app">
          <div className="content">
            <h1>Verifique a previsão do tempo em sua cidade</h1>
            <p className="lead">Digite o nome da sua cidade no campo abaixo</p>
            <input
              type="text"
              className="city-input"
              value={city}
              onChange={handleChange}
              placeholder="Digite a cidade"
            />
            <button className="search-button" onClick={handleSearch}>
              Pesquisar
            </button>

            {weatherForecast && (
              <div className="weather-info">
                <img src={weatherForecast.current.condition.icon} alt="Weather Icon" />
                <img src={getWeatherIcon()} alt="Weather Background" className="weather-background" />
                <h3>Atualmente em {city} o dia está: {weatherForecast.current.condition.text}</h3>
                <p className="loading">Temp: {weatherForecast.current.temp_c}º</p>
                <p className="lead">Umidade: {weatherForecast.current.humidity}</p>
                <p className="lead">País: {weatherForecast.location.country}</p>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
